class _TSFTheme{
    public function __construct(){
        // Add some constants
		$this->constants();

        // Additional Init Functions
		$this->include_action_hook();

        // Thaumbnail Theme Support
        add_theme_support( 'post-thumbnails' );

        if( function_exists('acf_add_options_page') ) {
            // add parent
            $parent = acf_add_options_page(array(
                'page_title' 	=> 'Theme Options',
                'menu_title' 	=> 'Theme Options',
                'redirect' 		=> false
            ));
        }

        register_nav_menus( 
            array(
                'primary_nav' => 'Primary Menu',
                'quick_links' => 'Quick Links'
            ) 
        );

        add_editor_style( 'assets/css/app.css' );
    }
    /**
	 * Define some constants variables
	 */
	public function constants(){
		define( '_TSF_THEME_DIR', get_stylesheet_directory() );
		define( '_TSF_THEME_URI', get_stylesheet_directory_uri() );
	}

    /**
	 * Include action hooks
	 */
	public function include_action_hook(){
		// Enqueue style and javascripts
		add_action( 'wp_enqueue_scripts', array( $this, 'enqueue_script_and_styles' ) );

        // Admin script
        add_action( 'admin_enqueue_scripts', array( $this, 'enqueue_scripts_styles' ) );

        // Register Custom Post Type
        add_action( 'init', 'wpdocs_codex_team_init' );
        add_action( 'init', 'wpdocs_codex_service_init' );

        // Dashboard Customize
        remove_action( 'welcome_panel', 'wp_welcome_panel' );

