        function remove_dashboard_widgets() {
            global $wp_meta_boxes;

            unset($wp_meta_boxes['dashboard']['side']['core']['dashboard_quick_press']);
            unset($wp_meta_boxes['dashboard']['side']['core']['dashboard_secondary']);
            unset($wp_meta_boxes['dashboard']['normal']['core']['dashboard_incoming_links']);
            unset($wp_meta_boxes['dashboard']['normal']['core']['dashboard_right_now']);
            unset($wp_meta_boxes['dashboard']['normal']['core']['dashboard_plugins']);
            unset($wp_meta_boxes['dashboard']['normal']['core']['dashboard_recent_drafts']);
            unset($wp_meta_boxes['dashboard']['normal']['core']['dashboard_recent_comments']);
            unset($wp_meta_boxes['dashboard']['side']['core']['dashboard_primary']);
        }

        // add_action('wp_dashboard_setup', 'remove_dashboard_widgets' );

        function r1_custom_welcome_screen_styles(){
            $externalStyles = '<link rel="stylesheet" href="'.get_stylesheet_directory_uri().'/dashboard-welcome-contents/dashboard.css">';
            echo $externalStyles;
        }

        add_action( 'admin_print_styles-index.php', 'r1_custom_welcome_screen_styles' );

    }

    /**
	 * Enqueue style and javascripts
	 */
    public function enqueue_script_and_styles(){
        // Load the Stylesheet
        wp_enqueue_style( 'r1-font-awesome-style',  'https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.7.2/css/all.min.css', array(), '');
        wp_enqueue_style( 'r1-theme-style',         _TSF_THEME_URI.'/style.css', array(), time() );
        wp_enqueue_style( 'r1-bulma-style',         _TSF_THEME_URI.'/assets/css/bulma.min.css', array(), '' );
        wp_enqueue_style( 'r1-slick-style',         _TSF_THEME_URI.'/assets/css/slick.min.css', array(), '' );
        wp_enqueue_style( 'r1-animate-style',       _TSF_THEME_URI.'/assets/css/animate.css', array(), time(), false );
        wp_enqueue_style( 'r1-fonts-style',         _TSF_THEME_URI.'/assets/css/fonts.css', array(), '' );
        wp_enqueue_style( 'r1-main-style',          _TSF_THEME_URI.'/assets/css/app.css', array(), time(), false );

        // Load the Scripts
        wp_enqueue_script(' r1-slick-scripts',      _TSF_THEME_URI .'/assets/js/slick.min.js', ['jquery'], '', true );
        wp_enqueue_script( 'r1-wow-scripts',        _TSF_THEME_URI . '/assets/js/wow.js', array('jquery'), time(), true);
        wp_enqueue_script( 'r1-main-scripts',       _TSF_THEME_URI . '/assets/js/app.js', array('jquery'), time(), true);

        if(is_page_template('tpl-about.php')){
        // wp_enqueue_script( 'r1-video-scripts',      _TSF_THEME_URI . '/assets/js/video-play.js', array('jquery'), time(), true);
        }

        wp_localize_script( 'TSF-main-scripts', 'ad_vars', array(
            'ajax_url' => admin_url( 'admin-ajax.php' ),
            'theme_uri' => _TSF_THEME_URI,
            'theme_dir' => _TSF_THEME_DIR,
            'site_url' => get_bloginfo('url')
        ));
    }

    /**
     * Enqueue scripts and styles
     */
    public function enqueue_scripts_styles(){
        // Enqueue Scripts
        wp_enqueue_script( 'admin-custom-scripts', _TSF_THEME_URI . '/dashboard-welcome-contents/script.js', array('jquery'), time(), true );

        wp_localize_script(
            'admin-custom-scripts',
            'TSF_scripts_js_vars',
            array(
                'ajax_url' => admin_url( 'admin-ajax.php' ),
                'stylesheet_url' => _TSF_THEME_URI,
                'stylesheet_dir' => _TSF_THEME_DIR
            )
        );
    }
}

new _TSFTheme();


