//? Team
function wpdocs_codex_team_init() {
	$labels = array(
		'name'                  => 'Our Teams',
		'singular_name'         => 'Our Team',
		'add_new'               => 'Add New',
		'add_new_item'          => 'Add New Team',
		'edit_item'             => 'Edit Our Team',
		'new_item'              => 'New Team',
		'all_items'             => 'All Teams',
		'view_item'             => 'View Our Team',
		'search_items'          => 'Search Our Teams',
		'not_found'             => 'No teams found.',
		'not_found_in_trash'    => 'No teams found in Trash.',
		'parent_item_colon'     => 'Parent Teams:',
        'menu_name'             => 'Our Team', 'Admin Menu text',
		'featured_image'        => 'Team Cover Image', 'Overrides the “Featured Image” phrase for this post type. Added in 4.3',
	);

	$args = array(
		'labels'             => $labels,
		'public'             => true,
		'publicly_queryable' => true,
		'show_ui'            => true,
		'show_in_menu'       => true,
		'query_var'          => true,
		'rewrite'            => array( 'slug' => 'our-team' ),
		'capability_type'    => 'post',
		'has_archive'        => false,
		'hierarchical'       => true,
		'menu_position'      => null,
        'show_in_rest' => true,
		'supports'           => array( 'title' ),
	);

	register_post_type( 'our-team', $args );
}

