if (!function_exists('write_log')) {
	function write_log($log) {
		if (true === WP_DEBUG) {
			if (is_array($log) || is_object($log)) {
				error_log(var_export($log, true));
			} else {
				error_log($log);
			}
		}
	}
}


function enqueue_custom_scripts() {
    wp_enqueue_script('custom-script', get_template_directory_uri() . '/assets/js/video-play.js', array('jquery'), rand(), true);

    $about_page_id = 10; 
    $video_id = get_field('youtube_video_id', $about_page_id); 

    wp_localize_script('custom-script', 'videoData', array(
        'videoID' => $video_id
    ));
}
add_action('wp_enqueue_scripts', 'enqueue_custom_scripts');



